import React, { useState } from 'react';
import { cn } from "@/lib/utils"; // Assuming lib/utils for cn
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { CheckCircle, Circle } from 'lucide-react';

// Define the steps for the wizard
const steps = [
  { id: 1, name: 'Create/Join Group', description: 'Start a new group or join an existing one.' },
  { id: 2, name: 'Invite Members', description: 'Send invites via email or link.' },
  { id: 3, name: 'Plan Itinerary', description: 'Collaboratively decide on activities.' },
  { id: 4, name: 'Confirm Participants', description: 'Finalize the group list.' },
  { id: 5, name: 'Group Payment', description: 'Manage costs and payments.' },
];

interface GroupBookingStepperProps {
  initialStep?: number;
}

const GroupBookingStepper: React.FC<GroupBookingStepperProps> = ({ initialStep = 1 }) => {
  const [currentStep, setCurrentStep] = useState<number>(initialStep);

  const handleNext = () => {
    setCurrentStep((prev) => Math.min(prev + 1, steps.length));
  };

  const handlePrev = () => {
    setCurrentStep((prev) => Math.max(prev - 1, 1));
  };

  return (
    <Card className="w-full max-w-3xl mx-auto my-8 animate-fade-in">
      <CardHeader>
        <CardTitle className="text-center text-xl md:text-2xl font-semibold text-primary">Group Booking Progress</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="flex flex-col md:flex-row items-start md:items-center justify-between space-y-4 md:space-y-0 md:space-x-4 mb-8 overflow-x-auto pb-4 no-scrollbar">
          {steps.map((step, index) => {
            const stepIndex = index + 1;
            const isActive = stepIndex === currentStep;
            const isCompleted = stepIndex < currentStep;

            return (
              <div key={step.id} className="flex items-center space-x-2 flex-shrink-0">
                <div
                  className={cn(
                    'flex items-center justify-center w-8 h-8 rounded-full border-2 transition-colors duration-300',
                    isCompleted ? 'bg-primary border-primary text-primary-foreground'
                      : isActive ? 'border-primary text-primary'
                      : 'border-border text-muted-foreground'
                  )}
                >
                  {isCompleted ? <CheckCircle size={16} /> : <span className="font-medium text-sm">{step.id}</span>}
                </div>
                <div className="flex flex-col">
                  <span className={cn(
                    'text-sm font-medium transition-colors duration-300',
                    isCompleted ? 'text-primary'
                      : isActive ? 'text-foreground'
                      : 'text-muted-foreground'
                  )}>
                    {step.name}
                  </span>
                  {/* Optional: Show description on hover or always */}
                  {/* <span className="text-xs text-muted-foreground hidden md:block">{step.description}</span> */}
                </div>
                {stepIndex < steps.length && (
                  <div className="hidden md:block h-px w-8 bg-border flex-grow mx-2"></div>
                )}
              </div>
            );
          })}
        </div>

        {/* Placeholder for the content of the current step */}
        <div className="mt-6 p-4 border border-border rounded-lg min-h-[150px] animate-slide-up bg-card">
          <h3 className="text-lg font-semibold mb-2">{steps[currentStep - 1].name}</h3>
          <p className="text-muted-foreground">{steps[currentStep - 1].description}</p>
          <p className="mt-4">Content for step {currentStep} goes here...</p>
        </div>

        {/* Navigation Buttons */}
        <div className="mt-8 flex justify-between">
          <Button onClick={handlePrev} disabled={currentStep === 1} variant="outline">
            Previous
          </Button>
          <Button onClick={handleNext} disabled={currentStep === steps.length}>
            Next
          </Button>
        </div>
      </CardContent>
    </Card>
  );
};

export default GroupBookingStepper;

import React, { useState } from 'react';
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/components/ui/use-toast"; // Assuming Shadcn's useToast hook
import { Mail, Link, Send, Copy } from 'lucide-react';
import { cn } from '@/lib/utils';

interface InviteControlsProps {
  groupId?: string; // Example prop
}

const InviteControls: React.FC<InviteControlsProps> = ({ groupId }) => {
  const [email, setEmail] = useState<string>('');
  const [inviteLink, setInviteLink] = useState<string>('');
  const [isGeneratingLink, setIsGeneratingLink] = useState<boolean>(false);
  const [isSendingEmail, setIsSendingEmail] = useState<boolean>(false);
  const { toast } = useToast();

  const handleSendEmailInvite = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!email) {
      toast({ title: "Error", description: "Please enter an email address.", variant: "destructive" });
      return;
    }
    setIsSendingEmail(true);
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 1000));
    console.log(`Sending invite to ${email} for group ${groupId}`);
    setIsSendingEmail(false);
    setEmail('');
    toast({ title: "Success", description: `Invite sent to ${email}.` });
  };

  const handleGenerateLink = async () => {
    setIsGeneratingLink(true);
    // Simulate API call to generate a link
    await new Promise(resolve => setTimeout(resolve, 1000));
    const generatedLink = `https://example.com/join?group=${groupId || 'demo123'}&token=${Math.random().toString(36).substring(7)}`;
    setInviteLink(generatedLink);
    setIsGeneratingLink(false);
    toast({ title: "Link Generated", description: "Shareable invite link created." });
  };

  const handleCopyLink = () => {
    navigator.clipboard.writeText(inviteLink)
      .then(() => {
        toast({ title: "Copied!", description: "Invite link copied to clipboard." });
      })
      .catch(err => {
        console.error('Failed to copy:', err);
        toast({ title: "Error", description: "Failed to copy link.", variant: "destructive" });
      });
  };

  return (
    <Card className="w-full animate-scale-in">
      <CardHeader>
        <CardTitle>Invite Members</CardTitle>
        <CardDescription>Add people to your trip group.</CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Email Invite Form */}
        <form onSubmit={handleSendEmailInvite} className="space-y-3">
          <Label htmlFor="email-invite" className="flex items-center">
            <Mail className="mr-2 h-4 w-4 text-muted-foreground" />
            Invite via Email
          </Label>
          <div className="flex space-x-2">
            <Input
              id="email-invite"
              type="email"
              placeholder="friend@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              disabled={isSendingEmail}
              required
              className="flex-grow"
            />
            <Button type="submit" disabled={isSendingEmail} className="whitespace-nowrap">
              <Send className="mr-2 h-4 w-4" />
              {isSendingEmail ? 'Sending...' : 'Send Invite'}
            </Button>
          </div>
        </form>

        {/* Invite Link Section */}
        <div className="space-y-3">
          <Label htmlFor="invite-link-input" className="flex items-center">
            <Link className="mr-2 h-4 w-4 text-muted-foreground" />
            Invite via Link
          </Label>
          {inviteLink ? (
            <div className="flex space-x-2">
              <Input
                id="invite-link-input"
                type="text"
                readOnly
                value={inviteLink}
                className="flex-grow bg-muted"
              />
              <Button variant="outline" size="icon" onClick={handleCopyLink} title="Copy link">
                <Copy className="h-4 w-4" />
              </Button>
            </div>
          ) : (
            <Button onClick={handleGenerateLink} disabled={isGeneratingLink} variant="secondary" className="w-full md:w-auto">
              <Link className="mr-2 h-4 w-4" />
              {isGeneratingLink ? 'Generating...' : 'Generate Invite Link'}
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

export default InviteControls;

import React from 'react';
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { UserCheck, UserX, UserClock, MailWarning } from 'lucide-react';
import { cn } from '@/lib/utils';

// Define member status types
type MemberStatus = 'accepted' | 'pending' | 'declined';

// Define the structure for a group member
interface GroupMember {
  id: string;
  name: string;
  avatarUrl?: string;
  status: MemberStatus;
}

interface GroupStatusTrackerProps {
  members: GroupMember[];
  onRemind?: (memberId: string) => void; // Optional callback for reminding
}

// Helper to get initials from name
const getInitials = (name: string): string => {
  return name
    .split(' ')
    .map(n => n[0])
    .slice(0, 2)
    .join('')
    .toUpperCase();
};

// Helper to get status styles and icon
const getStatusAttributes = (status: MemberStatus) => {
  switch (status) {
    case 'accepted':
      return {
        icon: <UserCheck className="h-4 w-4 mr-1" />,
        badgeVariant: 'success' as const, // Use specific literal type
        text: 'Accepted',
      };
    case 'pending':
      return {
        icon: <UserClock className="h-4 w-4 mr-1" />,
        badgeVariant: 'secondary' as const,
        text: 'Pending',
      };
    case 'declined':
