                </div>
                {!notification.read && (
                    <Button variant="ghost" size="icon" className="h-6 w-6" onClick={(e) => handleMarkRead(e, notification.id)} title="Mark as read">
                        <CheckCheck className="h-4 w-4 text-muted-foreground hover:text-primary"/>
                    </Button>
                )}
              </DropdownMenuItem>
            ))
          )}
        </ScrollArea>
      </DropdownMenuContent>
    </DropdownMenu>
  );
};

