const DashboardActionButtons: React.FC<DashboardActionButtonsProps> = ({
  onInviteFriends,
  onShareProfile,
  onManageInvites,
  onRedeemRewards,
  onRepeatBooking,
}) => {
  const actions = [
    { handler: onInviteFriends, label: 'Invite Friends', icon: UserPlus, condition: !!onInviteFriends },
