/**
 * Generalized Tokens & Base Styles for React Tailwind Projects
 * -----------------------------------------------------------
 * Generated based on application description: Trading Dashboard.
 */

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  /**
   * ========================================================================
   * Theme Variables (CSS Custom Properties)
   * ========================================================================
   */
  :root {
    /* Colors (Light Mode - HSL with PERCENT) */
    --background: 210 40% 98%;
    --foreground: 220 10% 10%;

    --card: 0 0% 100%;
    --card-foreground: 220 10% 10%;

    --popover: 0 0% 100%;
    --popover-foreground: 220 10% 10%;

    --primary: 220 75% 55%;
    --primary-foreground: 210 40% 98%;

    --secondary: 215 25% 65%;
    --secondary-foreground: 220 10% 25%;

    --muted: 210 40% 95%;
    --muted-foreground: 215 20% 55%;

    --accent: 220 85% 65%;
    --accent-foreground: 220 10% 10%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 98%;

    --success: 130 65% 40%;
    --success-foreground: 0 0% 98%;

    --warning: 40 90% 55%;
    --warning-foreground: 30 50% 15%;

    --border: 210 25% 88%;
    --input: 210 25% 88%;
    --ring: 220 75% 55%;

    /* Sidebar Colors */
    --sidebar-background: 210 35% 96%;
    --sidebar-foreground: 220 10% 10%;
    --sidebar-primary: 220 75% 55%;
    --sidebar-primary-foreground: 210 40% 98%;
    --sidebar-accent: 220 85% 65%;
    --sidebar-accent-foreground: 220 10% 10%;
    --sidebar-border: 210 25% 85%;
    --sidebar-ring: 220 75% 55%;

    /* Layout & Typography */
    --radius: 0.5rem;
    --font-family-base: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
    --font-family-heading: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
    --font-family-mono: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
    --text-scale: 1;
    --spacing-scale: 1;
    --transition-duration-base: 150ms;
  }

  /**
   * ========================================================================
   * Dark Mode Theme Variables
   * ========================================================================
   */
  .dark {
    --background: 220 15% 10%;
    --foreground: 210 40% 96%;

    --card: 220 15% 14%;
    --card-foreground: 210 40% 96%;

    --popover: 220 15% 12%;
    --popover-foreground: 210 40% 96%;

    --primary: 220 75% 65%;
    --primary-foreground: 220 15% 10%;

    --secondary: 215 20% 30%;
    --secondary-foreground: 210 40% 90%;

    --muted: 215 15% 25%;
    --muted-foreground: 215 15% 65%;

    --accent: 220 85% 70%;
    --accent-foreground: 220 15% 10%;

    --destructive: 0 75% 60%;
    --destructive-foreground: 0 0% 98%;

    --success: 130 60% 50%;
    --success-foreground: 130 100% 10%;

    --warning: 40 85% 60%;
    --warning-foreground: 40 100% 10%;

    --border: 215 15% 25%;
    --input: 215 15% 25%;
    --ring: 220 85% 70%;

    --sidebar-background: 220 15% 8%;
    --sidebar-foreground: 210 40% 96%;
    --sidebar-primary: 220 75% 65%;
    --sidebar-primary-foreground: 220 15% 10%;
    --sidebar-accent: 220 85% 70%;
    --sidebar-accent-foreground: 220 15% 10%;
    --sidebar-border: 215 15% 20%;
    --sidebar-ring: 220 85% 70%;
  }

  /* Base Element Styling */
  * {
    border-color: hsl(var(--border));
  }
  html {
    scroll-behavior: smooth;
  }
  body {
    background-color: hsl(var(--background));
    color: hsl(var(--foreground));
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-family: var(--font-family-base);
    font-feature-settings: "rlig" 1, "calt" 1, "ss01", "ss02", "cv01", "cv02", "cv03", "cv04", "cv11";
  }
  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-family-heading);
    color: hsl(var(--foreground));
  }
  h1 { font-size: calc(2.25rem * var(--text-scale, 1)); }
  h2 { font-size: calc(1.875rem * var(--text-scale, 1)); }
  h3 { font-size: calc(1.5rem * var(--text-scale, 1)); }
  h4 { font-size: calc(1.25rem * var(--text-scale, 1)); }
  p, span, button, a, li, input, textarea, label, div { font-size: calc(1rem * var(--text-scale, 1)); }
  .text-xs { font-size: calc(0.75rem * var(--text-scale, 1)); }
  .text-sm { font-size: calc(0.875rem * var(--text-scale, 1)); }
  .text-lg { font-size: calc(1.125rem * var(--text-scale, 1)); }
  .text-xl { font-size: calc(1.25rem * var(--text-scale, 1)); }
  .text-2xl { font-size: calc(1.5rem * var(--text-scale, 1)); }
}

@layer utilities {
  /**
   * ========================================================================
   * General Utilities
   * ========================================================================
   */
  .text-balance { text-wrap: balance; }
  .text-pretty { text-wrap: pretty; }
  .center {  display: flex; align-items: center; justify-content: center; }
  .text-gradient-primary {
    background: linear-gradient(to right, hsl(var(--primary)), hsl(var(--accent)));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    text-fill-color: transparent;
   }
  ::-webkit-scrollbar { width: 8px; height: 8px; }
  ::-webkit-scrollbar-track { background: transparent; }
  ::-webkit-scrollbar-thumb {
    background-color: hsla(var(--muted-foreground), 0.5);
    border-radius: var(--radius);
    border: 2px solid transparent;
    background-clip: padding-box;
  }
  ::-webkit-scrollbar-thumb:hover {
     background-color: hsla(var(--muted-foreground), 0.7);
  }
  .no-scrollbar::-webkit-scrollbar { display: none; }
  .no-scrollbar { -ms-overflow-style: none; scrollbar-width: none; }
}

@layer components {
  /**
   * ========================================================================
   * Common Component Base Styles & Animations
   * ========================================================================
   */
  @keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
  .animate-fade-in { animation: fadeIn 0.5s ease-out forwards; }

  @keyframes slideUp { from { opacity: 0; transform: translateY(20px); } to { opacity: 1; transform: translateY(0); } }
  .animate-slide-up { animation: slideUp 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards; }

  @keyframes scaleIn { from { opacity: 0; transform: scale(0.95); } to { opacity: 1; transform: scale(1); } }
  .animate-scale-in { animation: scaleIn 0.3s ease-out forwards; }

  @keyframes pulse { 0%, 100% { opacity: 1; } 50% { opacity: .5; } }
  .animate-pulse-gentle { animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite; }

  .stagger-item { opacity:0; animation: fadeIn 0.6s ease-out forwards; }
  .stagger-container > .stagger-item:nth-child(1) { animation-delay: 0.05s; }
  .stagger-container > .stagger-item:nth-child(2) { animation-delay: 0.1s; }
  .stagger-container > .stagger-item:nth-child(3) { animation-delay: 0.15s; }
  .stagger-container > .stagger-item:nth-child(4) { animation-delay: 0.2s; }
  .stagger-container > .stagger-item:nth-child(5) { animation-delay: 0.25s; }
}

import type { Config } from "tailwindcss";

export default {
    darkMode: ["class"], // Enables dark mode based on the 'class' attribute
    content: [
        "./pages/**/*.{ts,tsx}",
        "./components/**/*.{ts,tsx}",
        "./app/**/*.{ts,tsx}", // Common path for Next.js App Router
        "./src/**/*.{ts,tsx}",  // Common path for source files
        // Add any other paths where Tailwind classes might be used
    ],
    prefix: "", // No prefix for utility classes (e.g., 'text-primary' instead of 'tw-text-primary')
    theme: {
        // --- Container Settings ---
        // Default container configuration for centering and padding
        container: {
            center: true,
            padding: '2rem', // Default padding for containers
            screens: {
                '2xl': '1400px' // Max width for the container on 2xl screens
            }
        },

        // --- Theme Extensions ---
        // Use 'extend' to add customizations without overwriting Tailwind defaults
        extend: {
            // --- START: Project-Specific Font Families ---
            // Define custom font families based on CSS variables.
            fontFamily: {
                sans: ['var(--font-family-base)', 'system-ui', '-apple-system', 'BlinkMacSystemFont', '"Segoe UI"', 'Roboto', '"Helvetica Neue"', 'Arial', '"Noto Sans"', 'sans-serif', '"Apple Color Emoji"', '"Segoe UI Emoji"', '"Segoe UI Symbol"', '"Noto Color Emoji"'],
                heading: ['var(--font-family-heading)', 'system-ui', '-apple-system', 'BlinkMacSystemFont', '"Segoe UI"', 'Roboto', '"Helvetica Neue"', 'Arial', '"Noto Sans"', 'sans-serif', '"Apple Color Emoji"', '"Segoe UI Emoji"', '"Segoe UI Symbol"', '"Noto Color Emoji"'],
                mono: ['var(--font-family-mono)', 'ui-monospace', 'SFMono-Regular', 'Menlo', 'Monaco', 'Consolas', '"Liberation Mono"', '"Courier New"', 'monospace'],
            },
            // --- END: Project-Specific Font Families ---

            // --- Color Palette ---
            colors: {
                // Base colors (controlled by CSS variables for theming)
                border: 'hsl(var(--border))',
                input: 'hsl(var(--input))',
                ring: 'hsl(var(--ring))', // Used for focus rings, etc.
                background: 'hsl(var(--background))',
                foreground: 'hsl(var(--foreground))',
                primary: {
                    DEFAULT: 'hsl(var(--primary))',
                    foreground: 'hsl(var(--primary-foreground))' // Text color on primary background
                },
                secondary: {
                    DEFAULT: 'hsl(var(--secondary))',
                    foreground: 'hsl(var(--secondary-foreground))'
                },
                destructive: {
                    DEFAULT: 'hsl(var(--destructive))',
                    foreground: 'hsl(var(--destructive-foreground))'
                },
                muted: {
                    DEFAULT: 'hsl(var(--muted))',
                    foreground: 'hsl(var(--muted-foreground))' // Often used for subtle text or backgrounds
                },
                accent: {
                    DEFAULT: 'hsl(var(--accent))',
                    foreground: 'hsl(var(--accent-foreground))'
                },
                popover: {
                    DEFAULT: 'hsl(var(--popover))',
                    foreground: 'hsl(var(--popover-foreground))'
                },
                card: {
                    DEFAULT: 'hsl(var(--card))',
                    foreground: 'hsl(var(--card-foreground))'
                },
                // Sidebar specific colors (enabled as CSS variables are present)
                sidebar: {
                 DEFAULT: 'hsl(var(--sidebar-background))',
                 foreground: 'hsl(var(--sidebar-foreground))',
                 primary: 'hsl(var(--sidebar-primary))',
                 'primary-foreground': 'hsl(var(--sidebar-primary-foreground))',
                 accent: 'hsl(var(--sidebar-accent))',
                 'accent-foreground': 'hsl(var(--sidebar-accent-foreground))',
                 border: 'hsl(var(--sidebar-border))',
                 ring: 'hsl(var(--sidebar-ring))'
                },

                // --- START: Project-Specific Colors ---
                // Added based on CSS variables found in index.css
                success: {
                    DEFAULT: 'hsl(var(--success))',
                    foreground: 'hsl(var(--success-foreground))'
                },
                warning: {
                    DEFAULT: 'hsl(var(--warning))',
                    foreground: 'hsl(var(--warning-foreground))'
                }
                // --- END: Project-Specific Colors ---
            },

            // --- Border Radius ---
            // Uses CSS variable for easy global adjustment
            borderRadius: {
                lg: 'var(--radius)',
                md: 'calc(var(--radius) - 2px)',
                sm: 'calc(var(--radius) - 4px)'
            },

            // --- Keyframes for Animations ---
            keyframes: {
                // Common animations (e.g., for Accordion components)
                'accordion-down': {
                    from: { height: '0' },
                    to: { height: 'var(--radix-accordion-content-height)' }
                },
                'accordion-up': {
                    from: { height: 'var(--radix-accordion-content-height)' },
                    to: { height: '0' }
                },

                // --- START: Project-Specific Keyframes ---
                // Added based on @keyframes found in index.css
                fadeIn: { from: { opacity: '0' }, to: { opacity: '1' } },
                slideUp: { from: { opacity: '0', transform: 'translateY(20px)' }, to: { opacity: '1', transform: 'translateY(0)' } },
                scaleIn: { from: { opacity: '0', transform: 'scale(0.95)' }, to: { opacity: '1', transform: 'scale(1)' } },
                pulse: { '0%, 100%': { opacity: '1' }, '50%': { opacity: '.5' } },
                // --- END: Project-Specific Keyframes ---
            },

            // --- Animation Utilities ---
            animation: {
                // Common animations
                'accordion-down': 'accordion-down 0.2s ease-out',
                'accordion-up': 'accordion-up 0.2s ease-out',

                // --- START: Project-Specific Animations ---
                // Added based on animation classes found in index.css
                'fade-in': 'fadeIn 0.5s ease-out forwards',
                'slide-up': 'slideUp 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards',
                'scale-in': 'scaleIn 0.3s ease-out forwards',
                'pulse-gentle': 'pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite',
                // Note: Stagger uses 'fade-in' directly in CSS, no separate animation utility needed here.
                // --- END: Project-Specific Animations ---
            },

            // --- START: Project-Specific Box Shadows ---
            boxShadow: {
                // Added based on user prompt mention of 'distinct module cards'
                'card': '0 1px 3px rgba(0, 0, 0, 0.05), 0 1px 2px rgba(0, 0, 0, 0.1)',
            },
            // --- END: Project-Specific Box Shadows ---

            // --- START: Project-Specific Background Images ---
            backgroundImage: {
                // Add custom background images if needed
            },
            // --- END: Project-Specific Background Images ---
        }
    },

    // --- Tailwind CSS Plugins ---
    plugins: [
        require("tailwindcss-animate"), // Essential for the keyframes/animations defined above
        require('@tailwindcss/forms'), // Needed for form styling mentioned in prompt
        require('@tailwindcss/typography'), // Useful for potential content sections
        require('tailwind-scrollbar'), // Added because scrollbar styles were found in index.css
    ],
} satisfies Config;

import React from 'react';
import { Link } from 'react-router-dom';
import { Menu, User, Settings, Sun, Moon } from 'lucide-react';
import { cn } from '@/lib/utils'; // Assuming exists for clsx/tailwind-merge
import { Button } from '@/components/ui/button'; // Assuming shadcn/ui Button
// import { useTheme } from 'next-themes'; // Uncomment if theme toggle implemented

interface HeaderProps {
  toggleSidebar?: () => void; // Optional: Only needed if a collapsible sidebar exists
  hasCollapsibleSidebar?: boolean; // Flag to conditionally render menu toggle
}

const Header: React.FC<HeaderProps> = ({ toggleSidebar, hasCollapsibleSidebar }) => {
  // const { theme, setTheme } = useTheme(); // Uncomment for theme toggle

  return (
    <header className={cn(
      'sticky top-0 z-50 w-full',
      'border-b border-border',
      'bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60'
    )}>
      <div className="container flex h-14 max-w-screen-2xl items-center px-4">
        <div className="mr-4 flex items-center">
          {/* Conditionally render menu toggle button for collapsible sidebar */}
          {hasCollapsibleSidebar && (
            <Button
              variant="ghost"
              size="icon"
              className="mr-2 md:hidden" // Hide on medium screens and up where sidebar might become static
              onClick={toggleSidebar}
              aria-label="Toggle Sidebar"
            >
              <Menu className="h-5 w-5" />
            </Button>
          )}
          <Link to="/dashboard" className="mr-6 flex items-center space-x-2">
            {/* Placeholder Logo */}
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="h-6 w-6 text-primary">
              <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"></path>
            </svg>
            <span className="font-bold inline-block text-foreground">Ascendion</span>
          </Link>
        </div>

        {/* Placeholder Navigation (can be expanded) */}
        <nav className="hidden md:flex items-center space-x-6 text-sm font-medium">
          <Link
            to="/dashboard"
            className="transition-colors hover:text-foreground/80 text-foreground/60"
          >
            Dashboard
          </Link>
          <Link
            to="/market-overview"
            className="transition-colors hover:text-foreground/80 text-foreground/60"
          >
            Market
          </Link>
          {/* Add more links as needed */}
        </nav>

        <div className="flex flex-1 items-center justify-end space-x-2">
          {/* Placeholder User Actions */}
          {/* <Button
            variant="ghost"
            size="icon"
            onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}
            aria-label="Toggle Theme"
          >
            <Sun className="h-5 w-5 scale-100 dark:scale-0 transition-all" />
            <Moon className="h-5 w-5 absolute scale-0 dark:scale-100 transition-all" />
          </Button> */}
           <Button variant="ghost" size="icon" aria-label="Settings Placeholder">
            <Settings className="h-5 w-5 text-foreground/60 hover:text-foreground/80 transition-colors" />
          </Button>
          <Link to="/profile">
            <Button variant="ghost" size="icon" aria-label="User Profile">
              <User className="h-5 w-5 text-foreground/60 hover:text-foreground/80 transition-colors" />
            </Button>
          </Link>
        </div>
      </div>
    </header>
  );
};

export default Header;

import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { X, LayoutDashboard, User, LineChart, Settings } from 'lucide-react';
import { cn } from '@/lib/utils'; // Assuming exists for clsx/tailwind-merge
import { Button } from '@/components/ui/button'; // Assuming shadcn/ui Button

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

const navItems = [
  { href: '/dashboard', label: 'Dashboard', icon: LayoutDashboard },
  { href: '/market-overview', label: 'Market Overview', icon: LineChart },
  { href: '/profile', label: 'Profile', icon: User },
  { href: '/settings', label: 'Settings', icon: Settings }, // Example placeholder route
];

const Sidebar: React.FC<SidebarProps> = ({ isOpen, onClose }) => {
  const location = useLocation();

  return (
    <>
      {/* Overlay for mobile */}
      <div
        className={cn(
          'fixed inset-0 z-30 bg-black/50 transition-opacity duration-300 ease-in-out md:hidden',
          isOpen ? 'opacity-100' : 'opacity-0 pointer-events-none'
        )}
        onClick={onClose}
        aria-hidden="true"
      />

      {/* Sidebar */}
      <aside
        className={cn(
          'fixed inset-y-0 left-0 z-40 flex h-full w-64 flex-col',
          'border-r border-[hsl(var(--sidebar-border))]',
          'bg-[hsl(var(--sidebar-background))] text-[hsl(var(--sidebar-foreground))]',
          'transition-transform duration-300 ease-in-out',
          // Mobile/Overlay State
          'md:translate-x-0',
          isOpen ? 'translate-x-0' : '-translate-x-full',
          // Desktop Static/Collapsible State (adjust if needed for always-static desktop)
          'md:sticky md:top-0 md:z-30 md:block' // Adjust positioning as needed for desktop layout
          // If desktop should also collapse, add classes like:
          // 'md:transition-[width] md:duration-300 md:ease-in-out',
          // isOpen ? 'md:w-64' : 'md:w-16' // Example desktop collapse width
        )}
      >
        <div className="flex items-center justify-between p-4 border-b border-[hsl(var(--sidebar-border))]">
          {/* Sidebar Header / Logo Area */}
          <Link to="/dashboard" className="flex items-center space-x-2" onClick={onClose}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="h-6 w-6 text-[hsl(var(--sidebar-primary))]">
                <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"></path>
              </svg>
              <span className="font-bold">Ascendion</span>
          </Link>
          <Button
            variant="ghost"
            size="icon"
            className="md:hidden" // Only show close button on mobile/overlay
            onClick={onClose}
            aria-label="Close Sidebar"
          >
            <X className="h-5 w-5" />
          </Button>
        </div>

        {/* Navigation Links */}
        <nav className="flex-1 space-y-1 p-4 overflow-y-auto">
          {navItems.map((item) => {
            const isActive = location.pathname === item.href;
            return (
              <Link
                key={item.href}
                to={item.href}
                onClick={onClose} // Close sidebar on navigation (mobile)
                className={cn(
                  'group flex items-center rounded-md px-3 py-2 text-sm font-medium',
                  'transition-colors',
                  isActive
                    ? 'bg-[hsl(var(--sidebar-accent))] text-[hsl(var(--sidebar-accent-foreground))]'
                    : 'hover:bg-[hsl(var(--sidebar-accent))] hover:text-[hsl(var(--sidebar-accent-foreground))]'
                )}
              >
                <item.icon className={cn('mr-3 h-5 w-5', isActive ? 'text-[hsl(var(--sidebar-accent-foreground))]' : 'text-[hsl(var(--sidebar-foreground))] group-hover:text-[hsl(var(--sidebar-accent-foreground))]')} />
                <span>{item.label}</span>
              </Link>
            );
          })}
        </nav>

        {/* Optional Footer section in Sidebar */}
        {/* <div className="p-4 mt-auto border-t border-[hsl(var(--sidebar-border))] text-xs text-muted-foreground">
          Sidebar Footer Content
        </div> */}
      </aside>
    </>
  );
};

export default Sidebar;

import React from 'react';
