              {loading ? (
                 <span className="animate-pulse">Connecting...</span>
              ) : (
                <>
                  {Icon && <Icon className="mr-2 h-4 w-4" />}
                  {providerName}
                </>
              )}
            </Button>
          );
        })}
      </div>
    </div>
  );
};

