            fontSize={12} 
            tickLine={false} 
            axisLine={false}
            // Add formatting based on timeframe if needed
          />
          <YAxis 
            yAxisId="price" 
            stroke="hsl(var(--muted-foreground))" 
            fontSize={12} 
            tickLine={false} 
            axisLine={false} 
            tickFormatter={(value) => `$${value.toFixed(2)}`} 
